import{_ as e,c as a,o as s,a2 as t}from"./chunks/framework.T1JhP7-b.js";const l="/phpstorm/assets/create-new-files-normal.DCaqrnL_.gif",o="/phpstorm/assets/create-new-files-quickly-opend-file-step1.B4mGMNdL.png",i="/phpstorm/assets/create-new-files-quickly-opend-file-step2.CCFGqOOq.png",c="/phpstorm/assets/create-new-files-quickly-opend-file-step3.CFRAFxHD.png",r="/phpstorm/assets/create-new-files-quickly-step1.DhXMSvIi.png",d="/phpstorm/assets/create-new-files-quickly-step2.BwKd023L.png",m="/phpstorm/assets/create-new-files-quickly-step3.BnlnClji.png",p="/phpstorm/assets/create-new-files-quickly-using-terminal.BK6kFbor.png",n="/phpstorm/assets/create-new-files-quickly-using-run-command.Daie-tPj.png",u="/phpstorm/assets/create-new-files-quickly-using-run-command-show-make-list.C1fzy5Rk.png",x=JSON.parse('{"title":"快速创建文件","description":"","frontmatter":{},"headers":[],"relativePath":"guide/create-new-files-quickly.md","filePath":"guide/create-new-files-quickly.md","lastUpdated":1662308655000}'),h={name:"guide/create-new-files-quickly.md"},_=t('<h1 id="快速创建文件" tabindex="-1">快速创建文件 <a class="header-anchor" href="#快速创建文件" aria-label="Permalink to &quot;快速创建文件&quot;">​</a></h1><h2 id="一般方式创建文件" tabindex="-1">一般方式创建文件 <a class="header-anchor" href="#一般方式创建文件" aria-label="Permalink to &quot;一般方式创建文件&quot;">​</a></h2><p>普通方式创建文件，可以在左侧侧边栏点击对应的文件目录后按照下列步骤进行：</p><ol><li>点击右键，选中 <code>New</code></li><li>选中要创建的文件类型，比如这里的 <code>PHP Class</code> 文件</li><li>输入要创建的类名，核对命名空间等信息</li><li>点击 <strong>OK</strong> 或者按回车按键确认创建文件</li></ol><details class="details custom-block"><summary>点击切换动态图展示与隐藏</summary><p><img src="'+l+'" alt=""></p></details><h2 id="使用快捷键创建文件" tabindex="-1">使用快捷键创建文件 <a class="header-anchor" href="#使用快捷键创建文件" aria-label="Permalink to &quot;使用快捷键创建文件&quot;">​</a></h2><p>快捷方式创建文件的流程可以总结为：</p><ol><li>在编辑器中打开导航栏</li><li>新建文件</li></ol><p>具体由如下两种方式：</p><ul><li><p>方式一：如果当前打开了文件，则可以使用下面的工作流创建新文件。</p><ol><li><p>使用快捷键 <code>Command + ↑</code>呼出导航栏，其中 <code>↑</code> 为方向键上</p><details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+o+'" alt=""></p></details></li><li><p>使用快捷键 <code>Command + N</code> 后输入 <code>class</code> 关键字筛选要新建的文件类型</p><details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+i+'" alt=""></p></details></li><li><p>点击回车确认选择，在弹出层可以看到类命名空间和文件路径已经默认提供，输入对应的文件名即可点击回车确认</p><details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+c+'" alt=""></p></details></li></ol></li><li><p>方式二：如果当前未打开任何文件，则可以使用如下工作流创建新文件。</p><ol><li>使用快捷键 <code>Command + ↑</code>呼出导航栏，其中 <code>↑</code> 为方向键上<details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+r+'" alt=""></p></details></li><li>使用快捷键 <code>Command + N</code> 后输入 <code>class</code> 关键字筛选要新建的文件类型<details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+d+'" alt=""></p></details></li><li>点击回车确认选择，在弹出层中输入类名、命名空间、文件名以及文件路径等，点击回车确认<details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+m+'" alt=""></p></details></li></ol></li></ul><h2 id="laravel-新建常规文件" tabindex="-1">Laravel 新建常规文件 <a class="header-anchor" href="#laravel-新建常规文件" aria-label="Permalink to &quot;Laravel 新建常规文件&quot;">​</a></h2><p>如果当前使用的是 Laravel 项目，并且创建的是 Model模型、Job任务、Command命令等文件，使用下面的方式会更加优雅。</p><h3 id="在命令行终端创建" tabindex="-1">在命令行终端创建 <a class="header-anchor" href="#在命令行终端创建" aria-label="Permalink to &quot;在命令行终端创建&quot;">​</a></h3><p>在打开命令行终端（<code>Option(或 Alt)⌥ + F12</code>），输入对应的创建命令，比如： <code>php artisan make:model Post</code></p><details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+p+'" alt=""></p></details><h3 id="使用快捷键创建" tabindex="-1">使用快捷键创建 <a class="header-anchor" href="#使用快捷键创建" aria-label="Permalink to &quot;使用快捷键创建&quot;">​</a></h3><p>使用快捷键 <code>Command + Shift + X</code>，在弹出层中输入 <code>artisan make:model Post</code> 点击回车会自动在目录 <code>app/Models</code> 生成模型文件 <code>Post.php</code>，如下图所示：</p><p><img src="'+n+'" alt=""></p><h3 id="支持创建哪些文件" tabindex="-1">支持创建哪些文件 <a class="header-anchor" href="#支持创建哪些文件" aria-label="Permalink to &quot;支持创建哪些文件&quot;">​</a></h3><p>使用快捷键 <code>Command + Shift + X</code>，在弹出层中输入 <code>artisan make</code> 命令，可以看到对应命令下的所有可执行列表，使用上、下方向键进行选择即可。</p><details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><p><img src="'+u+'" alt=""></p></details>',21),k=[_];function f(y,g,q,b,w,P){return s(),a("div",null,k)}const v=e(h,[["render",f]]);export{x as __pageData,v as default};
