import{_ as s,c as a,o as i,a2 as e}from"./chunks/framework.zuJLNJjo.js";const n="/phpstorm/assets/close-search-man-page-index-in-terminal.L8jEhP1O.png",u=JSON.parse('{"title":"常用快捷键","description":"","frontmatter":{},"headers":[],"relativePath":"guide/some-important-keybindings.md","filePath":"guide/some-important-keybindings.md","lastUpdated":1666753306000}'),o={name:"guide/some-important-keybindings.md"},t=e('<h1 id="常用快捷键" tabindex="-1">常用快捷键 <a class="header-anchor" href="#常用快捷键" aria-label="Permalink to &quot;常用快捷键&quot;">​</a></h1><p>经过极简配置后的 PhpStorm 编辑器在主编辑界面几乎看不到操作按钮，此时使用快捷键操作能显著提升开发效率。</p><p>以下以 <strong>macOS</strong> 快捷键配置做演示，比如：</p><ul><li>切换项目代码：<code>Command + 1</code></li><li>查找操作：<code>Command + Shift + A</code></li><li>文件查找：<code>Command + Shift + O</code></li><li>查看函数或方法定义：<code>Command + B</code></li><li>切换文件定义：<code>Command + E</code></li><li>查看文件包含属性或方法：<code>Command + F12</code></li></ul><h2 id="查找操作-command-shift-a" tabindex="-1">查找操作 <code>Command + Shift + A</code> <a class="header-anchor" href="#查找操作-command-shift-a" aria-label="Permalink to &quot;查找操作 `Command + Shift + A`&quot;">​</a></h2><p>该命令在编辑器的 主菜单 -&gt; <code>Help</code> -&gt; <code>Find Action...</code> 后，可以在菜单的后面看到快捷键为：<code>Command + Shift + A</code>。</p><blockquote><p>如果执行上面的命令打开带有 <code>apropos</code> 搜索而不是 <code>查找操作</code></p></blockquote><p>对话框的终端，请<a href="https://intellij-support.jetbrains.com/hc/en-us/articles/360005137400-Cmd-Shift-A-hotkey-opens-Terminal-with-apropos-search-instead-of-the-Find-Action-dialog" target="_blank" rel="noreferrer">参考这里</a></p><blockquote><details class="details custom-block"><summary>点击切换截图展示与隐藏</summary><ol><li>打开 <code>系统菜单</code> | <code>系统偏好设置</code> | <code>键盘</code> | <code>快捷键</code> | <code>服务</code></li><li>禁用或 Search man Page Index in Terminal (or change the shortcut) <img src="'+n+'" alt=""></li></ol></details></blockquote><p>在上一节 <strong>极简配置</strong> 中介绍的对应配置如：标签位置 <code>Tab placement</code> 、工具窗口栏 <code>Tool Window Bars</code>、行号 <code>Line Numbers</code> 等等就可以通过这个快捷键进行搜索后切换显示配置。</p><h2 id="文件查找-command-shift-o" tabindex="-1">文件查找 <code>Command + Shift + O</code> <a class="header-anchor" href="#文件查找-command-shift-o" aria-label="Permalink to &quot;文件查找 `Command + Shift + O`&quot;">​</a></h2><p>该命令在编辑器的 主菜单 -&gt; <code>Navigate</code> -&gt; <code>File...</code> 后，可以在菜单的后面看到快捷键为：<code>Command + Shift + O</code>。</p><p>在实际开发编码时，经常会需要打开不同目录的不同文件。通过命令 <code>Command + Shift + O</code> 命令后输入对应文件所在目录/文件名称，或者直接输入文件名后在下拉列表处找到对应文件后点击回车确认打开文件即可。</p><h2 id="查看函数或方法定义-command-b" tabindex="-1">查看函数或方法定义 <code>Command + B</code> <a class="header-anchor" href="#查看函数或方法定义-command-b" aria-label="Permalink to &quot;查看函数或方法定义 `Command + B`&quot;">​</a></h2><p>该命令在编辑器的 主菜单 -&gt; <code>Navigate</code> -&gt; <code>Declaration or Usages</code> 后，可以在菜单的后面看到快捷键为：<code>Command + B</code>。</p><ol><li>在 PHP 文件中遇到函数或方法定义，想进入到函数或方法的定义处查看函数或方法运行的逻辑；</li><li>或想看下定义的方法在那些地方有调用。</li></ol><p>可以使用 <code>Command + B</code> 进入到对应的函数或者方法定义或者跳转到对应调用处。</p><h2 id="切换打开的文件记录-command-e" tabindex="-1">切换打开的文件记录 <code>Command + E</code> <a class="header-anchor" href="#切换打开的文件记录-command-e" aria-label="Permalink to &quot;切换打开的文件记录 `Command + E`&quot;">​</a></h2><p>该命令在编辑器的 主菜单 -&gt; <code>View</code> -&gt; <code>Recent Files</code> 后，可以在菜单的后面看到快捷键为：<code>Command + E</code>。</p><p>PhpStorm中默认会记录打开的文件，以便我们快速检索，可以使用 <code>Command + E</code> 快捷键快速跳转到近期打开的文件，选中文件后按回车重新打开之前打开过的文件。</p><p>这在我们频繁切换文件操作时非常高效。</p><h2 id="查看文件包含属性或方法-commnad-f12" tabindex="-1">查看文件包含属性或方法 <code>Commnad + F12</code> <a class="header-anchor" href="#查看文件包含属性或方法-commnad-f12" aria-label="Permalink to &quot;查看文件包含属性或方法 `Commnad + F12`&quot;">​</a></h2><p>该命令在编辑器的 主菜单 -&gt; <code>Navigate</code> -&gt; <code>File Structure</code> 后，可以在菜单的后面看到快捷键为：<code>Command + F12</code>。</p><p>针对PHP类和函数文件，可以使用 <code>Commnad + F12</code> 可以查看该类文件的成员变量和方法，再次按 <code>Command + F12</code> 可以一并列出父类的成员变量和方法。这个功能在查看源码时使用非常频繁。</p><h2 id="其他快捷操作" tabindex="-1">其他快捷操作 <a class="header-anchor" href="#其他快捷操作" aria-label="Permalink to &quot;其他快捷操作&quot;">​</a></h2><p>在编写代码逻辑时，常常会需要打印一些变量或者编写条件判断，可以使用 PhpStorm 的快捷操作：</p><div class="language-php vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki shiki-themes github-light github-dark has-focused-lines vp-code"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&lt;?</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">php</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$name </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;Curder&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">;</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$bool </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> true</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">;</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$user </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> [</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;name&#39;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> =&gt;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> $name, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;hobbies&#39;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> =&gt;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> [</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;coding&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;music&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">]];</span></span>\n<span class="line"></span>\n<span class="line has-focus"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$name</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">.</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">echo</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> # 按tab就会自动补全为 echo $name;</span></span>\n<span class="line has-focus"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$bool</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">.</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">if</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> # 按tab就会自动补全为 if 条件判断</span></span>\n<span class="line has-focus"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$user</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">.</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">var_dump</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> # 按tab会自动补全为 var_dump($user);</span></span>\n<span class="line has-focus"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">$isset</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">.</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">isset</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> # 按tab就会自动补全为if (isset($isset)) 条件判断</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">更多快捷键操作参考</p><p>选择主菜单上的 <code>Help</code> -&gt; <code>Keyboard Shortcuts PDF</code> 打开快捷键操作PDF文件预览</p></div>',28),d=[t];function l(h,p,c,r,k,m){return i(),a("div",null,d)}const E=s(o,[["render",l]]);export{u as __pageData,E as default};
